const { MedianFinder } = require('./solution');

test('basic sequence', () => {
    const mf = new MedianFinder();
    mf.addNum(1);
    mf.addNum(2);
    expect(mf.findMedian()).toBeCloseTo(1.5);
    mf.addNum(3);
    expect(mf.findMedian()).toBeCloseTo(2.0);
});

test('single element', () => {
    const mf = new MedianFinder();
    mf.addNum(5);
    expect(mf.findMedian()).toBeCloseTo(5.0);
});

test('even count', () => {
    const mf = new MedianFinder();
    mf.addNum(1);
    mf.addNum(2);
    mf.addNum(3);
    mf.addNum(4);
    expect(mf.findMedian()).toBeCloseTo(2.5);
});
