const { groupAnagrams } = require('./solution');

function sortGroups(groups) {
    return groups.map(g => g.slice().sort()).sort((a, b) => a[0].localeCompare(b[0]));
}

test('multiple groups', () => {
    const result = groupAnagrams(['eat','tea','tan','ate','nat','bat']);
    expect(sortGroups(result)).toEqual(sortGroups([['ate','eat','tea'],['bat'],['nat','tan']]));
});

test('empty string', () => {
    const result = groupAnagrams(['']);
    expect(result).toEqual([['']]);
});

test('single element', () => {
    const result = groupAnagrams(['a']);
    expect(result).toEqual([['a']]);
});
