const { ListNode, mergeKLists } = require('./solution');

function toLinked(arr) {
    let head = null;
    for (let i = arr.length - 1; i >= 0; i--) {
        head = new ListNode(arr[i], head);
    }
    return head;
}

function toArray(head) {
    const result = [];
    while (head) {
        result.push(head.val);
        head = head.next;
    }
    return result;
}

test('basic', () => {
    const lists = [toLinked([1,4,5]), toLinked([1,3,4]), toLinked([2,6])];
    expect(toArray(mergeKLists(lists))).toEqual([1,1,2,3,4,4,5,6]);
});

test('empty array', () => {
    expect(mergeKLists([])).toBeNull();
});

test('array with null', () => {
    expect(mergeKLists([null])).toBeNull();
});
