const { ListNode, mergeTwoLists } = require('./solution');

function toLinked(arr) {
    let head = null;
    for (let i = arr.length - 1; i >= 0; i--) {
        head = new ListNode(arr[i], head);
    }
    return head;
}

function toArray(head) {
    const result = [];
    while (head) {
        result.push(head.val);
        head = head.next;
    }
    return result;
}

test('basic', () => {
    expect(toArray(mergeTwoLists(toLinked([1,2,4]), toLinked([1,3,4])))).toEqual([1,1,2,3,4,4]);
});

test('both empty', () => {
    expect(toArray(mergeTwoLists(null, null))).toEqual([]);
});

test('one empty', () => {
    expect(toArray(mergeTwoLists(null, toLinked([0])))).toEqual([0]);
});
