const { ListNode, reverseList } = require('./solution');

function toLinked(arr) {
    let head = null;
    for (let i = arr.length - 1; i >= 0; i--) {
        head = new ListNode(arr[i], head);
    }
    return head;
}

function toArray(head) {
    const result = [];
    while (head) {
        result.push(head.val);
        head = head.next;
    }
    return result;
}

test('basic', () => {
    expect(toArray(reverseList(toLinked([1,2,3,4,5])))).toEqual([5,4,3,2,1]);
});

test('two', () => {
    expect(toArray(reverseList(toLinked([1,2])))).toEqual([2,1]);
});

test('empty', () => {
    expect(reverseList(null)).toBeNull();
});
