from solution import ListNode, merge_k_lists

def to_linked(lst):
    head = None
    for v in reversed(lst):
        head = ListNode(v, head)
    return head

def to_list(head):
    result = []
    while head:
        result.append(head.val)
        head = head.next
    return result

def test_basic():
    lists = [to_linked([1,4,5]), to_linked([1,3,4]), to_linked([2,6])]
    assert to_list(merge_k_lists(lists)) == [1,1,2,3,4,4,5,6]

def test_empty():
    assert merge_k_lists([]) is None

def test_single_empty():
    assert merge_k_lists([None]) is None
