from solution import ListNode, merge_two_lists

def to_linked(lst):
    head = None
    for v in reversed(lst):
        head = ListNode(v, head)
    return head

def to_list(head):
    result = []
    while head:
        result.append(head.val)
        head = head.next
    return result

def test_basic():
    assert to_list(merge_two_lists(to_linked([1,2,4]), to_linked([1,3,4]))) == [1,1,2,3,4,4]

def test_empty():
    assert to_list(merge_two_lists(None, None)) == []

def test_one_empty():
    assert to_list(merge_two_lists(None, to_linked([0]))) == [0]
